// THIS WILL RUN AFTER THE WEB FORM IS FULLY LOADED
frappe.web_form.after_load = function (web_form) {
    // 1: Hide Frappe's default buttons
    const actions = document.querySelector('.web-form-actions');
    if (actions) {
        actions.style.display = 'none';
    }

    // 2: Create your custom button
    const btn = document.createElement('button');
    btn.type = 'button';
    btn.className = 'btn btn-primary';
    btn.innerText = 'Login Sucessfully';
    btn.style.margin = '10px 0';

    // 3: Add button event listener
    btn.addEventListener('click', async () => {
        // Read form values
        const full = frappe.web_form.get_value('full_name');
        const email = frappe.web_form.get_value('email');
        const pwd = frappe.web_form.get_value('password');

        // Basic validation
        if (!full || !email || !pwd) {
            frappe.msgprint('All fields are required.');
            return;
        }

        try {
            await frappe.web_form.submit();
            frappe.msgprint('Registered successfully!');
            // Optional redirect:
            // window.location.href = '/thank-you';
        } catch (err) {
            console.error(err);
            frappe.msgprint('Failed to register. Please try again.');
        }
    });

    // 4: Append custom button to the footer
    const footer = document.querySelector('.web-form-footer');
    if (footer) {
        footer.appendChild(btn);
    }
};
